    private void calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcularActionPerformed
        String bina1, bina2, bina3, bina4;
        String celda = "";
        int valor=0;

        bina1 = bi1.getText();
        if (bina1.charAt(0) == '1') valor += 8;
        if (bina1.charAt(1) == '1') valor += 4;
        if (bina1.charAt(2) == '1') valor += 2;
        if (bina1.charAt(3) == '1') valor += 1;
        celda += Integer.toHexString(valor).toUpperCase();

        bina2 = bi2.getText();
        valor = 0; //Reiniciamos el valor
        if (bina2.charAt(0) == '1') valor += 8;
        if (bina2.charAt(1) == '1') valor += 4;
        if (bina2.charAt(2) == '1') valor += 2;
        if (bina2.charAt(3) == '1') valor += 1;
        celda += Integer.toHexString(valor).toUpperCase();

        bina3 = bi3.getText();
        valor = 0; //Reiniciamos el valor
        if (bina3.charAt(0) == '1') valor += 8;
        if (bina3.charAt(1) == '1') valor += 4;
        if (bina3.charAt(2) == '1') valor += 2;
        if (bina3.charAt(3) == '1') valor += 1;
        celda += Integer.toHexString(valor).toUpperCase();

        bina4 = bi4.getText();
        valor = 0; //Reiniciamos el valor
        if (bina4.charAt(0) == '1') valor += 8;
        if (bina4.charAt(1) == '1') valor += 4;
        if (bina4.charAt(2) == '1') valor += 2;
        if (bina4.charAt(3) == '1') valor += 1;
        celda += Integer.toHexString(valor).toUpperCase();

        System.out.println("El valor de la celda de memoria es: "+valor);
        System.out.println("El valor de la celda de memoria en hexadecimal es: "+celda);
        result.setText(celda);

    }//GEN-LAST:event_calcularActionPerformed

